						if (fontsize > 1000)
						{
							fontsize = 1000;
							
							textfield.setText(
							
							  String.valueOf(fontsize));
						}
						
						else if (fontsize == 0)
						{
							fontsize = 1;
						}
						
						font = new Font(font.getName(),
						
						    font.getStyle(), fontsize);
						
						textarea.setFont(font);
