	public Matrix[][] partition(int m, int n)
	{
		//  partitions a matrix into an m x n block matrix
		//
		//  | A11 | A12 | A13 | ... | A1n |
		//  |-----+-----+-----+-----+-----|
		//  | A21 | A22 | A23 | ... | A2n |
		//  |-----+-----+-----+-----+-----|
		//  | A31 | A32 | A33 | ... | A3n |
		//  |-----+-----+-----+-----+-----|
		//  | ... | ... | ... | ... | ... |
		//  |-----+-----+-----+-----+-----|
		//  | Am1 | Am2 | Am3 | ... | Amn |
		
		int rows = matrix   .length;
		int cols = matrix[0].length;
		
		int row_unit = rows / m;
		int col_unit = cols / n;
		
		Matrix[][] A = new Matrix[m][n];
		
		for (int i = 0; i < m; i++)
		for (int j = 0; j < n; j++)
		
		    A[i][j] = this.get(
		
			i*row_unit, j*col_unit,
			  row_unit,   col_unit);
		
		return A;
	}
	
	
